mapper.insert:
insert into 

notice(title, content, noticedate) 

values( #{title}, #{content}, #{noticeDate}  );

mapper.select:
select *

from notice

where noticeidx = #{noticeIdx} and title = #{title};

mapper.update:
update notice

set title = #{title}, content = #{content}, noticedate =#{noticeDate}, noticemodify = #{noticeModify}

where noticeidx = #{noticeIdx}

mapper.select:
select *

from notice

order by noticedate;

	select * 
    from member
    where role = #{role}
    order by name asc;;

select *
from reservation
where memberidx = #{memberIdx} and confirm = 'false';

select *
from shop
where owneridx = #{owneridx}
order by shopname asc;

select *
from item
where shopidx = #{shopidx}
order by itemname asc;

  		select *
  		from shop
		order by shopname asc;

   	select i.*, sum(r.number) as reservation_count from item as i
	join reservation as r
	on i.itemidx = r.itemidx
	where i.shopidx = #{shopidx}
	group by r.itemidx
	order by itemname asc;
